import React, { Component,useState, Fragment } from 'react'
import Category from "./Category";
import Addnews from "./Addnews";
// import React, { Component, useReducer } from "react";
// import Category from "./Category";
import axios from "axios";
import { Redirect, Link } from "react-router-dom";



export default class Dashboard extends Component {
    constructor(props) {
        super(props);
            this.state = {
                catname:"",
                user:"",
                file:null,
                success: false,
              };
              this.onChange = this.onChange.bind(this);
           }
           onChange(e) {
            this.setState({
              [e.target.name]: e.target.value,
            });
         }
         onChangeHandler = (e) => {
          this.setState({
            file: e.target.files[0],
          });
          console.log(e.target.files[0])
        };
         onSubmit = async (e) => {
            e.preventDefault();
                  //------------------------imge----------------
                  const data = new FormData();
                data.append("file", this.state.file);
                
                console.log(data);
                const token = sessionStorage.getItem("token");
                const config1 = {
                  headers: {
                    Authorization: `Bearer ${token}`,
                    "Content-Type": "multipart/form-data",
                  },
                };
               try{
                  const res = await axios.post(
                    `http://localhost:5000/api/v1/category/photo`,
                    data,
                    config1
                  );
                  console.log(res.data.data);
      //--------------------------------------------------------------------------------- 
            const add = {
                catname:this.state.catname,
                file:res.data.data,
                           
             };
            const body = JSON.stringify(add);
            const token = sessionStorage.getItem("token");
            const config = {
              headers: {
                Authorization: `Bearer ${token}`,
                "Content-Type": "application/json",
              },
              
           }
           console.log(token);
               const reslt = await axios.post(`http://localhost:5000/api/v1/category`,body,config);
                   this.setState({success: true,});
               }
               catch (error) 
               {alert("Sorry Something Wrong!!");}
           }
            render() {
              
              return (
                <Fragment>
                { this.state.success ?
                 ( <Redirect to="/Admin/mynewslist" />
                 ):
                 (
            <div className="content-wrapper ">
                <div className="row d-none" id="proBanner"></div>
              

                {/* User List */}
                <div className="row" id="vendor">
                    <div className="col-12 grid-margin">
                        <div className="card">
                            <div className="card-body">
                               <h3 className="mb-4">Add News</h3>
                                <form>
                                               
                                    <input type="text" placeholder="Enter News Title"></input>
                                    <textarea className="mt-5" placeholder="Enter Description"></textarea>
                                    {/* <label className="mt-3">Add Image</label>
                                         <input type="file" /> */}
                                    <input type="submit" value="Add" className="btn btn-info mt-5"/>
                                    
                                </form>   
                            </div>
                        </div>
                    </div>
                </div>
              </div>
              )};
              </Fragment>
        );
    }
}
